import { motion } from 'framer-motion';
import { Dumbbell } from 'lucide-react';
import { Link, NavLink } from 'react-router-dom';

const Header = () => {
  const activeClassName = "text-white font-semibold bg-primary-dark/30 px-3 py-1.5 rounded-md"; // Enhanced active state
  const inactiveClassName = "text-indigo-100 hover:text-white hover:bg-primary-dark/20 px-3 py-1.5 rounded-md transition-colors"; // Subtle hover bg

  const getNavLinkClass = ({ isActive }) => isActive ? activeClassName : inactiveClassName;

  return (
    <motion.header
      initial={{ y: -50, opacity: 0 }} // Start further up and faded
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.4, ease: 'easeOut' }}
      // Subtle gradient or solid color with shadow
      className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-md sticky top-0 z-50"
    >
      <div className="container mx-auto px-4 py-3"> {/* Slightly reduced padding */}
        <div className="flex items-center justify-between">
          <Link to="/" className="flex items-center group"> {/* Group for hover effect */}
            <motion.div
              className="flex items-center"
              whileHover={{ scale: 1.03 }} // Subtle scale on hover
              transition={{ type: 'spring', stiffness: 400, damping: 15 }}
            >
              <Dumbbell size={28} className="mr-2 group-hover:rotate-[-15deg] transition-transform duration-300" /> {/* Icon rotate on hover */}
              <h1 className="text-xl md:text-2xl font-bold tracking-tight">FitSplit</h1> {/* Tracking */}
            </motion.div>
          </Link>

          <nav>
             {/* Increased spacing, adjusted text size */}
            <ul className="flex space-x-1 md:space-x-2 text-sm md:text-base">
              <li>
                <NavLink to="/" className={getNavLinkClass} end>
                  Dashboard
                </NavLink>
              </li>
              <li>
                <NavLink to="/split" className={getNavLinkClass}>
                  My Split
                </NavLink>
              </li>
              <li>
                <NavLink to="/progress" className={getNavLinkClass}>
                  Progress
                </NavLink>
              </li>
              <li>
                <NavLink to="/profile" className={getNavLinkClass}>
                  Profile
                </NavLink>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
