import { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion'; // Import AnimatePresence
import ConsistencyContext from './context/ConsistencyContext';
import Header from './components/Header';
import Footer from './components/Footer';
import Dashboard from './pages/Dashboard';
import SplitPage from './pages/SplitPage';
// Import other pages if you add them (e.g., Profile)

function App() {
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(true);

  // Define fetch function once
  const fetchConsistencyScore = async () => {
    console.log('Fetching score...'); // Log start
    setLoading(true);
    setScore(null); // Reset score while fetching
    try {
      // Mock API call
      const response = await new Promise(resolve => {
        setTimeout(() => {
          const newScore = Math.floor(Math.random() * 100);
          console.log('Fetched score:', newScore); // Log result
          resolve({ score: newScore });
        }, 1500);
      });
      setScore(response.score);
    } catch (error) {
      console.error("Failed to fetch consistency score:", error);
      setScore(null); // Ensure score is null on error
    } finally {
      setLoading(false);
      console.log('Fetching complete.'); // Log end
    }
  };

  // Fetch score on initial mount
  useEffect(() => {
    fetchConsistencyScore();
  }, []); // Empty dependency array ensures it runs only once on mount

  const contextValue = {
    score,
    loading,
    refreshScore: fetchConsistencyScore // Provide the fetch function
  };

  return (
    <ConsistencyContext.Provider value={contextValue}>
      <div className="flex flex-col min-h-screen bg-gray-50">
        <Header />

        <main className="flex-grow">
          {/* AnimatePresence helps manage exit animations for routes */}
           <AnimatePresence mode='wait'>
            <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/split" element={<SplitPage />} />
                {/* Add other routes here */}
                {/* <Route path="/profile" element={<Profile />} /> */}
                 {/* Catch-all for 404 Not Found */}
                 <Route path="*" element={
                    <div className="container mx-auto px-4 py-16 text-center">
                       <h1 className="text-4xl font-bold text-red-600 mb-4">404 - Not Found</h1>
                       <p className="text-gray-700">The page you are looking for does not exist.</p>
                       <Link to="/" className="mt-6 inline-block px-6 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition-colors">
                           Go to Dashboard
                       </Link>
                    </div>
                 }/>
            </Routes>
           </AnimatePresence>
        </main>

        <Footer />
      </div>
    </ConsistencyContext.Provider>
  );
}

export default App;
