import { motion } from 'framer-motion';
import { Dumbbell } from 'lucide-react';
import { Link, NavLink } from 'react-router-dom'; // Import Link and NavLink

const Header = () => {
  const activeClassName = "text-white font-semibold";
  const inactiveClassName = "text-indigo-200 hover:text-white transition-colors";

  return (
    <motion.header
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
      className="bg-indigo-600 text-white shadow-lg sticky top-0 z-50" // Added sticky and z-index
    >
      <div className="container mx-auto px-4 py-4"> {/* Adjusted padding */}
        <div className="flex items-center justify-between">
          <Link to="/" className="flex items-center">
            <motion.div
              className="flex items-center"
              whileHover={{ scale: 1.05 }}
            >
              <Dumbbell size={28} className="mr-2" /> {/* Adjusted size/margin */}
              <h1 className="text-xl md:text-2xl font-bold">FitSplit</h1>
            </motion.div>
          </Link>

          <nav>
            <ul className="flex space-x-4 md:space-x-6 text-sm md:text-base">
              <li>
                <NavLink
                  to="/"
                  className={({ isActive }) => isActive ? activeClassName : inactiveClassName}
                  end // Important: Ensures "/" only matches the root path
                >
                  Dashboard
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/split"
                  className={({ isActive }) => isActive ? activeClassName : inactiveClassName}
                >
                  My Split
                </NavLink>
              </li>
              {/* Add more links like Profile if needed */}
              {/* <li>
                <NavLink
                  to="/profile"
                  className={({ isActive }) => isActive ? activeClassName : inactiveClassName}
                >
                  Profile
                </NavLink>
              </li> */}
            </ul>
          </nav>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
