import { useContext } from 'react';
import { motion } from 'framer-motion';
import { Activity, ArrowLeft } from 'lucide-react';
import { Link } from 'react-router-dom';
import ConsistencyContext from '../context/ConsistencyContext';
import LoadingState from '../components/LoadingState';
import BeginnerSplit from '../components/splits/BeginnerSplit';
import IntermediateSplit from '../components/splits/IntermediateSplit';
import AdvancedSplit from '../components/splits/AdvancedSplit';
import Card from '../components/ui/Card'; // Use Card for consistency

const SplitPage = () => {
  const { score, loading } = useContext(ConsistencyContext);

  const renderWorkoutSplit = () => {
    if (score === null) return null; // Don't render if score isn't loaded

    if (score >= 80) {
      return <AdvancedSplit />;
    } else if (score >= 40) {
      return <IntermediateSplit />;
    } else {
      return <BeginnerSplit />;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
       <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-6"
       >
        <Link to="/" className="inline-flex items-center text-indigo-600 hover:text-indigo-800 transition-colors group">
           <ArrowLeft size={18} className="mr-1 group-hover:-translate-x-1 transition-transform" />
           Back to Dashboard
        </Link>
      </motion.div>

       <motion.h1
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.1 }}
        className="flex items-center text-3xl font-bold text-gray-800 mb-8"
      >
        <Activity className="mr-3 text-indigo-600" size={32}/>
        Your Recommended Workout Split
      </motion.h1>

      {loading ? (
         <Card className="flex items-center justify-center">
            <LoadingState />
         </Card>
      ) : score !== null ? (
          renderWorkoutSplit()
      ) : (
        // Handle case where score failed to load - basic message
        <Card className="p-6 text-center">
            <p className="text-red-600">Could not load your workout split. Please try refreshing.</p>
        </Card>
      )}
    </div>
  );
};

export default SplitPage;
