import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Activity, BarChart2, User, ArrowRight } from 'lucide-react';
import ConsistencyContext from '../context/ConsistencyContext';
import ScoreDisplay from '../components/ScoreDisplay';
import LoadingState from '../components/LoadingState';
import Card from '../components/ui/Card';
import { getScoreInfo } from '../utils/helpers';

const Dashboard = () => {
  const { score, loading } = useContext(ConsistencyContext);
  const { level: recommendedLevel, splitColor } = getScoreInfo(score);

  const levelColors = { /* ... same as before ... */ };
  const buttonColor = splitColor ? levelColors[splitColor] : levelColors.gray;

  const pageVariants = { /* Optional: Add page transition variants */ };
  const transition = { /* Optional: Add transition */ };

  return (
    // Optional: Add motion.div for page transition
    <div className="container mx-auto px-4 py-8">
      <motion.h1
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        className="text-3xl font-bold text-gray-800 mb-8"
      >
        Dashboard
      </motion.h1>

      {loading ? (
        <Card className="flex items-center justify-center min-h-[400px]"> {/* Increased min-height */}
            <LoadingState />
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {/* Score Card */}
          <ScoreDisplay />

          {/* Recommended Split Card */}
          <Card className="flex flex-col justify-between">
            {/* ... content same as before ... */}
             <Link
              to="/split"
              className={`block w-full text-center p-4 bg-white border-t ${buttonColor} transition-colors duration-200 font-medium text-indigo-600 rounded-b-lg hover:bg-opacity-75`} // Added hover effect
            >
              View My {recommendedLevel} Split <ArrowRight className="inline ml-1" size={16} />
            </Link>
          </Card>

          {/* Progress Tracking Card - Updated Link */}
          <Card className="flex flex-col justify-between">
            <div className="p-6">
              <div className="flex items-center mb-3">
                <BarChart2 className="mr-2 text-blue-600" />
                <h3 className="text-xl font-semibold text-gray-800">Progress Tracking</h3>
              </div>
              <p className="text-gray-600 mb-4">
                 Visualize your consistency score history and track your improvement.
              </p>
            </div>
             <Link
              to="/progress" // Link to the new progress page
              className="block w-full text-center p-4 bg-white border-t border-blue-500 hover:bg-blue-50 transition-colors duration-200 font-medium text-blue-600 rounded-b-lg"
            >
              View Progress Graph <ArrowRight className="inline ml-1" size={16} />
            </Link>
          </Card>

          {/* Profile Card - Updated Link */}
          <Card className="flex flex-col justify-between">
             <div className="p-6">
               <div className="flex items-center mb-3">
                 <User className="mr-2 text-purple-600" />
                 <h3 className="text-xl font-semibold text-gray-800">Profile Settings</h3>
               </div>
               <p className="text-gray-600 mb-4">
                 View your account details and current recommended fitness level.
               </p>
             </div>
              <Link
               to="/profile" // Link to the new profile page
               className="block w-full text-center p-4 bg-white border-t border-purple-500 hover:bg-purple-50 transition-colors duration-200 font-medium text-purple-600 rounded-b-lg"
             >
               Go to Profile <ArrowRight className="inline ml-1" size={16} />
             </Link>
          </Card>

        </div>
      )}
    </div>
  );
};

export default Dashboard;
