import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Activity } from 'lucide-react';
import LoadingState from './components/LoadingState';
import BeginnerSplit from './components/splits/BeginnerSplit';
import IntermediateSplit from './components/splits/IntermediateSplit';
import AdvancedSplit from './components/splits/AdvancedSplit';
import ConsistencyContext from './context/ConsistencyContext';
import ScoreDisplay from './components/ScoreDisplay';
import Footer from './components/Footer';
import Header from './components/Header';

function App() {
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchConsistencyScore();
  }, []);

  const fetchConsistencyScore = async () => {
    setLoading(true);
    try {
      // Mock API call - replace with actual backend fetch
      const response = await new Promise(resolve => {
        setTimeout(() => {
          // Simulating backend response with random score for demo
          resolve({ score: Math.floor(Math.random() * 100) });
        }, 1500);
      });
      setScore(response.score);
    } catch (error) {
      console.error("Failed to fetch consistency score:", error);
    } finally {
      setLoading(false);
    }
  };

  const renderWorkoutSplit = () => {
    if (score >= 80) {
      return <AdvancedSplit />;
    } else if (score >= 40) {
      return <IntermediateSplit />;
    } else {
      return <BeginnerSplit />;
    }
  };

  return (
    <ConsistencyContext.Provider value={{ score, loading, refreshScore: fetchConsistencyScore }}>
      <div className="min-h-screen bg-gray-50">
        <Header />

        <main className="container mx-auto px-4 py-8">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="max-w-4xl mx-auto"
          >
            {loading ? (
              <LoadingState />
            ) : (
              <>
                <ScoreDisplay />
                <div className="mt-8">
                  <h2 className="flex items-center text-2xl font-bold text-gray-800 mb-6">
                    <Activity className="mr-2 text-indigo-600" />
                    Your Recommended Workout Split
                  </h2>
                  {renderWorkoutSplit()}
                </div>
              </>
            )}
          </motion.div>
        </main>

        <Footer />
      </div>
    </ConsistencyContext.Provider>
  );
}

export default App;
