import { motion } from 'framer-motion';
import { Dumbbell } from 'lucide-react';
import { Link, NavLink, useLocation } from 'react-router-dom'; // Import useLocation

const Header = () => {
  const location = useLocation(); // Get current location

  const activeClassName = "text-white font-semibold";
  const inactiveClassName = "text-indigo-200 hover:text-white transition-colors";

  // Function to determine class based on active state
  const getNavLinkClass = ({ isActive }) => isActive ? activeClassName : inactiveClassName;

  return (
    <motion.header
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
      className="bg-indigo-600 text-white shadow-lg sticky top-0 z-50"
    >
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          <Link to="/" className="flex items-center">
            <motion.div
              className="flex items-center"
              whileHover={{ scale: 1.05 }}
            >
              <Dumbbell size={28} className="mr-2" />
              <h1 className="text-xl md:text-2xl font-bold">FitSplit</h1>
            </motion.div>
          </Link>

          <nav>
            <ul className="flex space-x-3 md:space-x-6 text-sm md:text-base">
              <li>
                <NavLink to="/" className={getNavLinkClass} end>
                  Dashboard
                </NavLink>
              </li>
              <li>
                <NavLink to="/split" className={getNavLinkClass}>
                  My Split
                </NavLink>
              </li>
              <li> {/* New Link */}
                <NavLink to="/progress" className={getNavLinkClass}>
                  Progress
                </NavLink>
              </li>
              <li> {/* New Link */}
                <NavLink to="/profile" className={getNavLinkClass}>
                  Profile
                </NavLink>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
