import { useContext, useEffect } from 'react';
import { motion } from 'framer-motion';
import { RefreshCw, Award } from 'lucide-react';
import confetti from 'canvas-confetti';
import ConsistencyContext from '../context/ConsistencyContext';
import { getScoreInfo } from '../utils/helpers'; // Import helper

const ScoreDisplay = () => {
  const { score, refreshScore, loading } = useContext(ConsistencyContext); // Get loading state

  const { color, bgColor, ringColor, message } = getScoreInfo(score);

  useEffect(() => {
    // Trigger confetti for high scores only after loading is complete
    if (!loading && score !== null && score >= 80) {
      confetti({
        particleCount: 100,
        spread: 70,
        origin: { y: 0.6 },
        zIndex: 1000 // Ensure confetti is above other elements
      });
    }
  }, [score, loading]); // Depend on loading state

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className={`${bgColor} p-6 rounded-lg shadow-md border border-gray-200`} // Added border
    >
      <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
        {/* Score Info */}
        <div className="flex-grow">
            <div className="flex items-center mb-2">
                <Award size={24} className={`${color} mr-2`} />
                <h3 className="text-lg font-semibold text-gray-800">Your Consistency Score</h3>
            </div>
            <p className={`${color} text-sm sm:text-base font-medium`}>
              {loading ? 'Loading score...' : message}
            </p>
        </div>

        {/* Score Circle and Refresh */}
        <div className="flex items-center gap-3">
          <motion.div
            key={score} // Re-animate when score changes
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ type: 'spring', stiffness: 300, damping: 15 }}
            className={`flex items-center justify-center h-20 w-20 ${ringColor} ring-4 rounded-full bg-white shadow-inner`}
            whileHover={{ scale: 1.05 }}
          >
            {loading ? (
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-indigo-600"></div>
            ) : (
              <span className={`text-3xl font-bold ${color}`}>{score ?? '-'}</span>
            )}
          </motion.div>

          <motion.button
            whileHover={{ scale: 1.1, rotate: 90 }} // Rotate further on hover
            whileTap={{ scale: 0.9, rotate: 0 }} // Reset rotate on tap
            onClick={refreshScore}
            disabled={loading} // Disable button while loading
            className={`p-3 bg-white rounded-full shadow-md hover:shadow-lg transition-all duration-200 ease-out ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            aria-label="Refresh score"
          >
            <RefreshCw size={20} className={`text-gray-600 ${loading ? 'animate-spin' : ''}`} />
          </motion.button>
        </div>
      </div>
    </motion.div>
  );
};

export default ScoreDisplay;
