import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Activity, BarChart2, User, ArrowRight } from 'lucide-react';
import ConsistencyContext from '../context/ConsistencyContext';
import ScoreDisplay from '../components/ScoreDisplay';
import LoadingState from '../components/LoadingState';
import Card from '../components/ui/Card'; // Import Card
import { getScoreInfo } from '../utils/helpers'; // Import helper

const Dashboard = () => {
  const { score, loading } = useContext(ConsistencyContext);
  const { level: recommendedLevel, splitColor } = getScoreInfo(score);

  const levelColors = {
      green: 'border-green-500 hover:bg-green-50',
      yellow: 'border-yellow-500 hover:bg-yellow-50',
      red: 'border-red-500 hover:bg-red-50',
      gray: 'border-gray-500 hover:bg-gray-50', // Fallback
  }
  const buttonColor = splitColor ? levelColors[splitColor] : levelColors.gray;

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.h1
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        className="text-3xl font-bold text-gray-800 mb-8"
      >
        Dashboard
      </motion.h1>

      {loading ? (
        <Card className="flex items-center justify-center">
            <LoadingState />
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {/* Score Card */}
          <ScoreDisplay />

          {/* Recommended Split Card */}
          <Card className="flex flex-col justify-between">
            <div className="p-6">
              <div className="flex items-center mb-3">
                <Activity className={`mr-2 text-${splitColor}-600`} />
                <h3 className="text-xl font-semibold text-gray-800">Recommended Split</h3>
              </div>
              <p className="text-gray-600 mb-4">
                Based on your score of {score}, we recommend the{' '}
                <span className={`font-medium text-${splitColor}-700`}>{recommendedLevel}</span> split.
              </p>
            </div>
            <Link
              to="/split"
              className={`block w-full text-center p-4 bg-white border-t ${buttonColor} transition-colors duration-200 font-medium text-indigo-600 rounded-b-lg`}
            >
              View My {recommendedLevel} Split <ArrowRight className="inline ml-1" size={16} />
            </Link>
          </Card>

          {/* Placeholder Cards for Future Features */}
          <Card className="p-6">
            <div className="flex items-center mb-3">
              <BarChart2 className="mr-2 text-blue-600" />
              <h3 className="text-xl font-semibold text-gray-800">Progress Tracking</h3>
            </div>
            <p className="text-gray-600 mb-4">
              (Coming Soon) Visualize your workout history and consistency over time.
            </p>
            <button className="mt-auto text-sm text-blue-600 font-medium opacity-50 cursor-not-allowed">View Progress</button>
          </Card>

          <Card className="p-6">
            <div className="flex items-center mb-3">
              <User className="mr-2 text-purple-600" />
              <h3 className="text-xl font-semibold text-gray-800">Profile Settings</h3>
            </div>
            <p className="text-gray-600 mb-4">
              (Coming Soon) Manage your account details and preferences.
            </p>
             <button className="mt-auto text-sm text-purple-600 font-medium opacity-50 cursor-not-allowed">Manage Profile</button>
          </Card>

        </div>
      )}
    </div>
  );
};

export default Dashboard;
