import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { LayoutDashboard, Activity, BarChart3, User, ArrowRight } from 'lucide-react';
import ConsistencyContext from '../context/ConsistencyContext'; // Ensure correct path
import ScoreDisplay from '../components/ScoreDisplay';      // Ensure correct path
import LoadingState from '../components/LoadingState';      // Ensure correct path
import Card from '../components/ui/Card';                 // Ensure correct path
import { getScoreInfo } from '../utils/helpers';           // Ensure correct path

const Dashboard = () => {
  // Destructure context, getting userData and loading state
  const { userData, loading } = useContext(ConsistencyContext);
  // Get score and profile from userData
  const { score, profile } = userData;

  // Pass the current user's score to getScoreInfo
  const { level: recommendedLevel, splitColor } = getScoreInfo(score);

  // --- Color Styles --- (Keep this mapping)
  const colorStyles = {
      green: { border: 'border-green-500', text: 'text-green-600', hoverBg: 'hover:bg-green-50', icon: Activity },
      yellow: { border: 'border-yellow-500', text: 'text-yellow-600', hoverBg: 'hover:bg-yellow-50', icon: Activity },
      red: { border: 'border-red-500', text: 'text-red-600', hoverBg: 'hover:bg-red-50', icon: Activity },
      blue: { border: 'border-blue-500', text: 'text-blue-600', hoverBg: 'hover:bg-blue-50', icon: BarChart3 }, // Progress
      purple: { border: 'border-purple-500', text: 'text-purple-600', hoverBg: 'hover:bg-purple-50', icon: User }, // Profile
      gray: { border: 'border-gray-300', text: 'text-gray-600', hoverBg: 'hover:bg-gray-50', icon: Activity }
  }
  const splitStyle = colorStyles[splitColor] || colorStyles.gray;
  const progressStyle = colorStyles.blue;
  const profileStyle = colorStyles.purple;
  // --- End Color Styles ---

  // --- Animation Variants --- (Keep this)
  const cardVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: i => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: i * 0.1, // Stagger animation
        duration: 0.4,
        ease: 'easeOut'
      }
    })
  };
  // --- End Animation Variants ---

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Welcome message using profile from userData */}
       <motion.h1
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.1, duration: 0.4 }}
        className="text-3xl font-bold text-gray-800 mb-1"
      >
         Welcome back, <span className="text-indigo-600">{profile.name}!</span>
       </motion.h1>
       <p className="text-gray-500 mb-8">Here's your dashboard overview.</p>


      {/* Show loading state based on context */}
      {loading ? (
        <Card className="flex items-center justify-center min-h-[400px]">
            <LoadingState message="Loading Dashboard..." />
        </Card>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-8">
          {/* Score Card - It uses context internally, so no props needed */}
           <motion.div className="lg:col-span-2" custom={0} initial="hidden" animate="visible" variants={cardVariants}>
            <ScoreDisplay />
           </motion.div>

          {/* Recommended Split Card - Uses score and recommendedLevel */}
          <motion.div custom={1} initial="hidden" animate="visible" variants={cardVariants}>
            <Card className="flex flex-col justify-between h-full" hoverEffect={true}>
                <div className="p-6">
                <div className="flex items-center mb-3">
                    <splitStyle.icon className={`mr-2.5 ${splitStyle.text}`} size={22} />
                    <h3 className="text-xl font-semibold text-gray-800">Recommended Split</h3>
                </div>
                {/* Display score and level */}
                <p className="text-gray-600 text-sm mb-4">
                    Based on your score of {score ?? 'N/A'}, we recommend the{' '}
                    <span className={`font-medium ${splitStyle.text}`}>{recommendedLevel}</span> split.
                </p>
                </div>
                <Link
                to="/split"
                className={`block w-full text-center p-4 mt-auto bg-white border-t ${splitStyle.border} ${splitStyle.hoverBg} transition-colors duration-200 font-medium ${splitStyle.text} rounded-b-xl`}
                >
                View My {recommendedLevel} Split <ArrowRight className="inline ml-1" size={16} />
                </Link>
            </Card>
          </motion.div>

          {/* Progress Tracking Card */}
          <motion.div custom={2} initial="hidden" animate="visible" variants={cardVariants}>
            <Card className="flex flex-col justify-between h-full" hoverEffect={true}>
                 <div className="p-6">
                   <div className="flex items-center mb-3">
                     <progressStyle.icon className={`mr-2.5 ${progressStyle.text}`} size={22}/>
                     <h3 className="text-xl font-semibold text-gray-800">Progress Tracking</h3>
                   </div>
                   <p className="text-gray-600 text-sm mb-4">
                      Visualize your consistency score history and track your improvement over time.
                   </p>
                 </div>
                 <Link
                   to="/progress"
                   className={`block w-full text-center p-4 mt-auto bg-white border-t ${progressStyle.border} ${progressStyle.hoverBg} transition-colors duration-200 font-medium ${progressStyle.text} rounded-b-xl`}
                 >
                   View Progress Graph <ArrowRight className="inline ml-1" size={16} />
                 </Link>
            </Card>
          </motion.div>

           {/* Profile Card */}
           <motion.div className="lg:col-span-2" custom={3} initial="hidden" animate="visible" variants={cardVariants}>
             <Card className="flex flex-col sm:flex-row items-center justify-between p-6" hoverEffect={true}>
                 <div className="flex items-center mb-4 sm:mb-0">
                    <profileStyle.icon className={`mr-3 ${profileStyle.text}`} size={24} />
                    <div>
                        <h3 className="text-xl font-semibold text-gray-800">Your Profile</h3>
                        <p className="text-gray-600 text-sm">View your details and current fitness level.</p>
                    </div>
                 </div>
                 <Link
                   to="/profile"
                   className={`inline-flex items-center mt-auto px-5 py-2.5 border ${profileStyle.border} ${profileStyle.hoverBg} transition-colors duration-200 font-medium ${profileStyle.text} rounded-lg text-sm`}
                 >
                   Go to Profile <ArrowRight className="ml-1.5" size={16} />
                 </Link>
             </Card>
           </motion.div>

        </div>
      )}
    </div>
  );
};

export default Dashboard;
