import { createContext } from 'react';

// Define initial state structure more explicitly
const initialUserState = {
  score: null,
  scoreHistory: [],
  profile: {
    name: 'Guest',
    email: '',
    joinDate: '',
    rfid: null // Add rfid to profile structure
  }
};

const ConsistencyContext = createContext({
  // User specific data
  userData: initialUserState, // Combine user-specific data
  // App state
  loading: true,
  isLoggedIn: false, // Track login status
  // Functions
  loginUser: async (rfid) => {}, // Function to handle login
  logoutUser: () => {},        // Function to handle logout
  refreshScore: async () => {} // Might need adjustment later if refresh needs RFID
});

export { initialUserState }; // Export for use in App.jsx
export default ConsistencyContext;
